import { makeStyles } from "@material-ui/core";

const backDropPadding = '2%';

export default makeStyles ((theme) => ({

    newsBar : {
        justifyContent: 'center',
    },
    newsBarBackDrop: {
        paddingLeft: backDropPadding,
        paddingRight: '2%',
    }

}));
