import React, { useState } from 'react';
import { Container, Grow, Grid, AppBar, TextField, Button, Paper, Typography } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import { useHistory, useLocation } from 'react-router-dom';
import ChipInput from 'material-ui-chip-input';

import { getPostsBySearch } from '../../actions/posts';
import Player from '../Player/Player';
import Pagination from '../Pagination';
import useStyles from './styles';

import '../../assets/audio/mias3.mp3'
import './home.css';


const Home = () => {
    const classes = useStyles();
 
    const [currentId, setCurrentId] = useState(0);
    const dispatch = useDispatch();

    const history = useHistory();
  
    const [audio] = useState(new Audio('../../assets/audio/mias3.mp3'));
    audio.play();
    
    return (
      <Grow in>
        <Container maxWidth="xl" className={classes.Container}>
            
            <Player url={'../../assets/audio/mias3.mp3'}></Player>
            <Typography variant='h1'>It's 2044.</Typography>
            <Typography variant='h4'>And Japan has been reduced to Hokkaido.</Typography>         
            <Grid container justify="space-between" alignItems="stretch" spacing={3} className={classes.gridContainer}>
                
            <Grid item xs={12} sm={6} md={9}>
                </Grid>
            <Grid item xs={12} sm={6} md={3}>

            </Grid>
          </Grid>
        </Container>
      </Grow>
    );
  };
  
  export default Home;
