//Have drawer that resizes and disappears for smaller devices
import React, { useRef, useState } from 'react';
import { Container, Grow, Grid, Paper, SwipeableDrawer, Button, List, Box, Icon} from '@material-ui/core';

import ListItemIcon from '@material-ui/icons/ListOutlined';
import ListIcon from '@material-ui/icons/List';
import { useDispatch } from 'react-redux';
import { useHistory, useLocation } from 'react-router-dom';


import useStyles from './styles';



const ControlPanel = () => {

    const classes = useStyles();
    const [currentId, setCurrentId] = useState(0);
    const [anchor, setAnchor] = useState('right');
    
    //TODO make this configureable
    const anchorSetting = 'right';
    const [state, setState] = React.useState({
        top: false,
        left: false,
        bottom: false,
        right: false,
      });
    
    const toggleDrawer = (anchor, open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
    
        setState({ ...state, [anchor]: open });
    };

    
    return (
        <Paper className={classes.paper} >
            
            <Box
            sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 'inherit' }}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
            ></Box>
            
            <React.Fragment key={anchor}>
                
                <Button onClick={toggleDrawer(anchor, true)} sx={{just}}><ListIcon/>Media</Button>
                <SwipeableDrawer
                anchor={anchor}
                open={state[anchor]}
                onClose={toggleDrawer(anchor, false)}
                onOpen={toggleDrawer(anchor, true)}
                >
                    <Button>Test</Button>
                </SwipeableDrawer>
            </React.Fragment>
        </Paper>
    )

}

export default ControlPanel;
