import { makeStyles } from "@material-ui/core";

//constant values
const backDropPadding = '2%';

export default makeStyles ((theme) => ({

    newsBar : {
        justifyContent: 'center',
    },
    newsBarBackDrop: {
        paddingLeft: backDropPadding,
        paddingRight: backDropPadding,
        [theme.breakpoints.down('xs')] : {
            display: 'none'
        }
    }

}));
